// Получить все данные из ноды по имени
const nodeData = $("loop name").all();

// Вернуть данные для следующей ноды
return nodeData;

const { parsePhoneNumber } = require('libphonenumber-js');

return $input.all().map(item => {
  const result = { json: { ...item.json } }; // Копируем все данные

  try {
    // === ОБРАБОТКА ТЕЛЕФОНА ===
    let phone = String(item.json.phone || '');

    // Удаляем все символы кроме цифр
    phone = phone.replace(/[^0-9]/g, '');

    // Если остались цифры, добавляем + в начало
    if (phone) {
      phone = '+' + phone;

      // Валидируем через libphonenumber
      const parsedPhone = parsePhoneNumber(phone);

      if (parsedPhone?.isValid?.()) {
        result.json.phone = parsedPhone.format('E.164');
      } else {
        result.json.phone = null;
      }
    } else {
      result.json.phone = null;
    }

  } catch {
    result.json.phone = null;
  }

  try {
    // === ВАЛИДАЦИЯ EMAIL ===
    const email = String(item.json.email || '').trim();

    // Простая регулярка для валидации email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (email && emailRegex.test(email)) {
      result.json.email = email.toLowerCase();
    } else {
      result.json.email = null;
    }

  } catch {
    result.json.email = null;
  }

  return result;
});
